     1e*(c3*dsin(5.d0*y)+e*(c4*dsin(7.d0*y)+e*(c5*dsin(9.d0*y)+e*(c6*dsisigm-034
     2n(11.d0*y)+e*c7*dsin(13.d0*y)))))))/x                             sigm-035
      if (l.eq.1) go to 5                                               sigm-036
