 1030 format (/' inelastic scattering to the target state',i3,'  spin ='resu-639
     1,f5.1,a1)                                                         resu-640
 1031 format (/6x,'inelastic cross section =',f14.6,' millibarns.')     resu-641
 1032 format (/'  total elastic cross section =',f14.6,' millibarns.')  resu-642
