 1040 format (/' angular distribution of compound scattering on levels',resu-650
     1i3,' to',i3//6x,'angle',6(i6,'-level',f5.1,a1))                   resu-651
 1041 format (1x,f10.3,6f18.7)                                          resu-652
 1042 format (/6x,'angle ',6(4a4,a2))                                   resu-653
