      if (ke.eq.3) ke=2                                                 fite-262
      if (ke.eq.2) go to 43                                             fite-263
      ke=1                                                              fite-264
      yy(3)=w(4)                                                        fite-265
      go to 64                                                          fite-266
c only one variable x                                                   fite-267
   35 if (iz(3).gt.1) go to 36                                          fite-268
      ke=0                                                              fite-269
      w(6)=yy(1)*e(1)                                                   fite-270
      w(7)=e(1)                                                         fite-271
      w(8)=0.d0                                                         fite-272
   36 nw(2)=iz(2)                                                       fite-273
      w(4)=x(1)                                                         fite-274
      w(5)=chi2                                                         fite-275
      call fit1(ke,nw,w(4))                                             fite-276
