   41 ke=ke+1                                                           fite-300
   42 ke=ke+1                                                           fite-301
   43 ke=ke+1                                                           fite-302
      ke=ke+1                                                           fite-303
c restore optimum values to x and f                                     fite-304
   44 do 45 i=1,m                                                       fite-305
   45 f(i)=w(jm+i)                                                      fite-306
      do 46 i=1,n                                                       fite-307
      x(i)=w(jm+m+i)                                                    fite-308
   46 w(i)=0.d0                                                         fite-309
      yy(3)=0.d0                                                        fite-310
