      if (ke*(ke-3).ne.0.or.(ke.eq.3.and.((yy(3).eq.0.d0.and.iz(3).le.n)fite-312
     1))) go to 63                                                      fite-313
c computation of the errors of the variables - restore matrix g         fite-314
      kv=min0(k,iz(3))                                                  fite-315
      go to 13                                                          fite-316
